mapper.update:
UPDATE  BOARDINFO
SET     TITLE       = #{title}
    , CONTENT       = #{content}
    , WRITER        = #{writer}
    , WRITERID      = #{writerId}
    , ISNOTICE      = #{isNotice}
    , ISSECRET      = #{isSecret}
    , ISDRAFT       = #{isDraft}
    , STATUS        = #{status}
    , PARENTID      = #{parentId}
    , DEPTH         = #{depth}
    , ATTACHMENTS   = #{attachments}
    , IMAGES        = #{images}
    , UPDDATE       = FORMATDATETIME(NOW(), 'yyyy-MM-dd hh:mm:ss')
WHERE   SEQNO       = #{seqNo}
