mapper.select:
SELECT * FROM users
WHERE   id = #{id}
  AND     SLEEPFRAG = FALSE

mapper.update:
UPDATE  users
SET     name    = #{name} ,
        pw      = #{pw},
        addr    = #{addr} ,
        upddate = #{updDate}
WHERE   id      = #{id}

UPDATE  users
SET     sleepfrag   = NOT (SELECT sleepfrag FROM users WHERE id = #{id})
WHERE   id          = #{id}
